<!-- Solo necesitamos un tooltip para toda la página -->
<div class="tooltip"></div>

<script>
    document.addEventListener('DOMContentLoaded', function () {
        // El contenedor principal que envuelve AMBAS vistas
        const mainWrapper = document.querySelector('.viewer-grid');
        // Seleccionamos TODAS las partes del cuerpo de AMBOS SVGs a la vez
        const bodyParts = document.querySelectorAll('.body-part');
        const tooltip = document.querySelector('.tooltip');

        bodyParts.forEach(part => {
            // Se activa cuando el cursor ENTRA a una parte (frontal O trasera)
            part.addEventListener('mouseover', function () {
                // Añadimos la clase al contenedor principal para atenuar todo
                mainWrapper.classList.add('is-interacting');

                const name = part.dataset.name;
                if (name) {
                    tooltip.textContent = name;
                    tooltip.style.opacity = '1';
                }
            });

            // Se activa cuando el cursor SALE de esa misma parte
            part.addEventListener('mouseout', function () {
                // Quitamos la clase del contenedor principal para que todo vuelva a la normalidad
                mainWrapper.classList.remove('is-interacting');
                tooltip.style.opacity = '0';
            });

            // Mueve el tooltip mientras el cursor está sobre la parte
            part.addEventListener('mousemove', function (e) {
                tooltip.style.left = (e.clientX + 15) + 'px';
                tooltip.style.top = (e.clientY - 15) + 'px';
            });
        });
    });
</script>

